import React, { useState, useEffect } from 'react';
import API from '../api';

export default function LessonPlayer({ lesson, user, onClose }) {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [rate, setRate] = useState(0.9);

  useEffect(() => {
    return () => stopSpeak();
  }, []);

  function speak(text) {
    if (!('speechSynthesis' in window)) return alert('TTS not supported in this browser');
    stopSpeak();
    const u = new SpeechSynthesisUtterance(text);
    u.lang = 'en-US';
    u.rate = rate;
    u.onend = () => setIsSpeaking(false);
    setIsSpeaking(true);
    window.speechSynthesis.speak(u);
  }

  function stopSpeak() {
    window.speechSynthesis.cancel();
    setIsSpeaking(false);
  }

  async function markComplete() {
    if (!user) return alert('Please login to save progress');
    try {
      await API('/api/progress', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: user.id,
          userRole: user.role,
          lesson: lesson._id,
          completed: true,
          score: 100
        })
      });
      alert('Progress saved');
    } catch (err) {
      alert('Could not save progress');
    }
  }

  return (
    <div className="card" style={{ marginTop: 12 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <h4>{lesson.title}</h4>
        <div>
          <button className="btn" onClick={onClose}>Close</button>
        </div>
      </div>

      <p style={{ fontSize: 20 }}>{lesson.textContent}</p>

      <div style={{ display: 'flex', gap: 8, alignItems: 'center' }}>
        <button className="btn" onClick={() => speak(lesson.textContent)}>
          {isSpeaking ? 'Speaking...' : 'Read Aloud'}
        </button>
        <button className="btn" style={{ background: '#6b7bff' }} onClick={stopSpeak}>
          Stop
        </button>

        {lesson.audioUrl && (
          <audio controls src={lesson.audioUrl} style={{ marginLeft: 20 }}></audio>
        )}

        <div style={{ marginLeft: 'auto' }}>
          <label className="small">Rate</label>
          <input
            type="range"
            min="0.6"
            max="1.4"
            step="0.1"
            value={rate}
            onChange={e => setRate(Number(e.target.value))}
          />
        </div>
      </div>

      <div style={{ marginTop: 12 }}>
        <button className="btn" onClick={markComplete}>Mark Complete</button>
      </div>
    </div>
  );
}
