import React, { useState, useEffect } from 'react';
import API from '../api';

export default function Teacher({ user }) {
  const [lessons, setLessons] = useState([]);
  const [newLesson, setNewLesson] = useState({
    title: '',
    textContent: '',
    grade: 1,
    subject: '',
    audioUrl: ''
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchLessons();
  }, []);

  async function fetchLessons() {
    try {
      const data = await API('/api/lessons');
      setLessons(data);
    } catch (err) {
      console.error('Error fetching lessons:', err);
    }
  }

  async function createLesson(e) {
    e.preventDefault();
    if (!newLesson.title || !newLesson.textContent) {
      return alert('Please fill in title and content');
    }

    setLoading(true);
    try {
      await API('/api/lessons', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newLesson)
      });
      
      alert('Lesson created successfully!');
      setNewLesson({
        title: '',
        textContent: '',
        grade: 1,
        subject: '',
        audioUrl: ''
      });
      fetchLessons();
    } catch (err) {
      alert('Error creating lesson: ' + err.message);
    } finally {
      setLoading(false);
    }
  }

  async function deleteLesson(id) {
    if (!window.confirm('Are you sure you want to delete this lesson?')) return;
    
    try {
      await API(`/api/lessons/${id}`, { method: 'DELETE' });
      alert('Lesson deleted');
      fetchLessons();
    } catch (err) {
      alert('Error deleting lesson');
    }
  }

  return (
    <div className="card" style={{ marginTop: 20 }}>
      <h3>Teacher Dashboard</h3>
      
      <div style={{ marginTop: 20, padding: 15, background: '#f5f5f5', borderRadius: 8 }}>
        <h4>Create New Lesson</h4>
        <form onSubmit={createLesson} style={{ display: 'grid', gap: 12 }}>
          <div>
            <label style={{ display: 'block', marginBottom: 4, fontWeight: 'bold' }}>
              Lesson Title *
            </label>
            <input
              type="text"
              placeholder="e.g., Learning Alphabets"
              value={newLesson.title}
              onChange={(e) => setNewLesson({ ...newLesson, title: e.target.value })}
              required
              style={{ width: '100%', padding: 8 }}
            />
          </div>

          <div>
            <label style={{ display: 'block', marginBottom: 4, fontWeight: 'bold' }}>
              Lesson Content *
            </label>
            <textarea
              placeholder="Enter the lesson text content..."
              rows="5"
              value={newLesson.textContent}
              onChange={(e) => setNewLesson({ ...newLesson, textContent: e.target.value })}
              required
              style={{ width: '100%', padding: 8 }}
            />
          </div>

          <div>
            <label style={{ display: 'block', marginBottom: 4, fontWeight: 'bold' }}>
              Grade Level *
            </label>
            <select
              value={newLesson.grade}
              onChange={(e) => setNewLesson({ ...newLesson, grade: Number(e.target.value) })}
              style={{ width: '100%', padding: 8 }}
            >
              <option value={1}>Grade 1</option>
              <option value={2}>Grade 2</option>
              <option value={3}>Grade 3</option>
            </select>
          </div>

          <div>
            <label style={{ display: 'block', marginBottom: 4, fontWeight: 'bold' }}>
              Subject
            </label>
            <input
              type="text"
              placeholder="e.g., English, Math, Science"
              value={newLesson.subject}
              onChange={(e) => setNewLesson({ ...newLesson, subject: e.target.value })}
              style={{ width: '100%', padding: 8 }}
            />
          </div>

          <div>
            <label style={{ display: 'block', marginBottom: 4, fontWeight: 'bold' }}>
              Audio URL (Optional) 🎵
            </label>
            <input
              type="text"
              placeholder="/audio/grade1/lesson-name.mp3"
              value={newLesson.audioUrl}
              onChange={(e) => setNewLesson({ ...newLesson, audioUrl: e.target.value })}
              style={{ width: '100%', padding: 8 }}
            />
            <small style={{ color: '#666', display: 'block', marginTop: 4 }}>
              📁 Place MP3 files in: <code style={{ background: '#fff', padding: '2px 6px', borderRadius: 4 }}>frontend/public/audio/grade1/</code>
              <br />
              📝 Then enter: <code style={{ background: '#fff', padding: '2px 6px', borderRadius: 4 }}>/audio/grade1/your-file.mp3</code>
            </small>
          </div>

          <button 
            type="submit" 
            className="btn" 
            disabled={loading}
            style={{ marginTop: 12 }}
          >
            {loading ? 'Creating...' : '✓ Create Lesson'}
          </button>
        </form>
      </div>

      <div style={{ marginTop: 30 }}>
        <h4>Existing Lessons ({lessons.length})</h4>
        {lessons.length === 0 ? (
          <p className="small">No lessons created yet.</p>
        ) : (
          <div style={{ display: 'grid', gap: 12, marginTop: 12 }}>
            {lessons.map((lesson) => (
              <div key={lesson._id} className="lesson" style={{ padding: 12 }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start' }}>
                  <div style={{ flex: 1 }}>
                    <strong style={{ fontSize: 16 }}>{lesson.title}</strong>
                    <div className="small" style={{ marginTop: 4 }}>
                      Grade {lesson.grade} {lesson.subject && `• ${lesson.subject}`}
                    </div>
                    <p className="small" style={{ marginTop: 8 }}>
                      {lesson.textContent.substring(0, 100)}...
                    </p>
                    {lesson.audioUrl && (
                      <div style={{ marginTop: 8, padding: 6, background: '#e8f5e9', borderRadius: 4, display: 'inline-block' }}>
                        <span style={{ fontSize: 12, color: '#2e7d32' }}>
                          🎵 Audio: {lesson.audioUrl}
                        </span>
                      </div>
                    )}
                  </div>
                  <button
                    className="btn"
                    onClick={() => deleteLesson(lesson._id)}
                    style={{ background: '#f44336', marginLeft: 12 }}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
