import React from 'react';
import { useNavigate } from 'react-router-dom';
export default function RoleSelect({ user }){
  const nav = useNavigate();
  if(!user) return <div className="card" style={{marginTop:20}}>Please login first.</div>
  return (
    <div className="card" style={{marginTop:20}}>
      <h3>Select type</h3>
      <div style={{display:'flex',gap:12,marginTop:12}}>
        <button className="btn" onClick={()=>nav('/challenge')}>Continue</button>
      </div>
      <p className="small">You logged in as <strong>{user.role}</strong>. Use the challenge selection next to choose Dyslexia.</p>
    </div>
  );
}
