import React, { useEffect, useState } from 'react';
import API from '../api';
import LessonPlayer from './LessonPlayer';
import { useNavigate } from 'react-router-dom';

export default function DyslexiaHome({ user }) {
  const [lessons, setLessons] = useState([]);
  const [allLessons, setAllLessons] = useState([]);
  const [grade, setGrade] = useState('all');
  const [subject, setSubject] = useState('all');
  const [search, setSearch] = useState('');
  const [selected, setSelected] = useState(null);
  const [progress, setProgress] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const nav = useNavigate();

  useEffect(() => {
    fetchLessons();
    if (user) {
      fetchProgress();
      fetchStats();
    }
  }, []);

  useEffect(() => {
    filterLessons();
  }, [grade, subject, search, allLessons]);

  async function fetchLessons() {
    try {
      setLoading(true);
      const data = await API(`/api/lessons?challenge=dyslexia`);
      setAllLessons(data);
      setLessons(data);
    } catch (err) {
      alert('Could not load lessons');
    } finally {
      setLoading(false);
    }
  }

  async function fetchProgress() {
    try {
      const data = await API(`/api/progress/${user.id}`);
      setProgress(data);
    } catch (err) {
      console.error('Could not load progress');
    }
  }

  async function fetchStats() {
    try {
      const data = await API(`/api/progress/stats/${user.id}`);
      setStats(data);
    } catch (err) {
      console.error('Could not load stats');
    }
  }

  function filterLessons() {
    let filtered = [...allLessons];

    // Filter by grade
    if (grade !== 'all') {
      filtered = filtered.filter(l => l.grade === Number(grade));
    }

    // Filter by subject
    if (subject !== 'all') {
      filtered = filtered.filter(l => l.subject?.toLowerCase() === subject.toLowerCase());
    }

    // Filter by search
    if (search) {
      filtered = filtered.filter(l =>
        l.title.toLowerCase().includes(search.toLowerCase()) ||
        l.textContent.toLowerCase().includes(search.toLowerCase())
      );
    }

    setLessons(filtered);
  }

  function isLessonCompleted(lessonId) {
    return progress.some(p => p.lesson?._id === lessonId && p.completed);
  }

  function getGradeColor(grade) {
    const colors = {
      1: 'linear-gradient(135deg, #ffeaa7 0%, #fdcb6e 100%)',
      2: 'linear-gradient(135deg, #74b9ff 0%, #0984e3 100%)',
      3: 'linear-gradient(135deg, #fab1a0 0%, #ff7675 100%)'
    };
    return colors[grade] || colors[1];
  }

  function getGradeEmoji(grade) {
    const emojis = { 1: '🌟', 2: '⭐', 3: '✨' };
    return emojis[grade] || '📚';
  }

  function getSubjectEmoji(subject) {
    const emojis = {
      english: '📖',
      math: '🔢',
      science: '🔬',
      art: '🎨'
    };
    return emojis[subject?.toLowerCase()] || '📚';
  }

  // Group lessons by unit
  function groupLessonsByUnit(lessons) {
    const grouped = {};
    lessons.forEach(lesson => {
      const unit = lesson.unit || 0;
      if (!grouped[unit]) grouped[unit] = [];
      grouped[unit].push(lesson);
    });
    return grouped;
  }

  const groupedLessons = groupLessonsByUnit(lessons);
  const recentCompletions = progress
    .filter(p => p.completed)
    .sort((a, b) => new Date(b.lastAccessed) - new Date(a.lastAccessed))
    .slice(0, 3);

  return (
    <div className="card" style={{ marginTop: 20 }}>
      {/* Header Section */}
      <div style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: 30,
        borderRadius: 20,
        color: 'white',
        marginBottom: 30,
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div style={{
          position: 'absolute',
          top: -50,
          right: -50,
          width: 200,
          height: 200,
          background: 'rgba(255,255,255,0.1)',
          borderRadius: '50%',
          filter: 'blur(40px)'
        }}></div>

        <div style={{ position: 'relative', zIndex: 1 }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: 15, marginBottom: 15 }}>
            <div style={{ fontSize: '3.5em' }}>📚</div>
            <div>
              <h3 style={{ margin: 0, fontSize: '2.5em' }}>Dyslexia Learning</h3>
              <p style={{ margin: 0, opacity: 0.9, fontSize: '1.2em' }}>
                Learn at your own pace with audio support!
              </p>
            </div>
          </div>

          {/* Progress Stats */}
          {user && stats && (
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
              gap: 15,
              marginTop: 20
            }}>
              <div style={{
                background: 'rgba(255,255,255,0.2)',
                padding: 15,
                borderRadius: 15,
                textAlign: 'center'
              }}>
                <div style={{ fontSize: '2em' }}>🎯</div>
                <div style={{ fontSize: '2em', fontWeight: 'bold' }}>{stats.completionPercentage}%</div>
                <div style={{ fontSize: '0.9em', opacity: 0.9 }}>Progress</div>
              </div>
              <div style={{
                background: 'rgba(255,255,255,0.2)',
                padding: 15,
                borderRadius: 15,
                textAlign: 'center'
              }}>
                <div style={{ fontSize: '2em' }}>✅</div>
                <div style={{ fontSize: '2em', fontWeight: 'bold' }}>{stats.completedLessons}</div>
                <div style={{ fontSize: '0.9em', opacity: 0.9 }}>Completed</div>
              </div>
              <div style={{
                background: 'rgba(255,255,255,0.2)',
                padding: 15,
                borderRadius: 15,
                textAlign: 'center'
              }}>
                <div style={{ fontSize: '2em' }}>📖</div>
                <div style={{ fontSize: '2em', fontWeight: 'bold' }}>{stats.totalLessons}</div>
                <div style={{ fontSize: '0.9em', opacity: 0.9 }}>Total Lessons</div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Search and Filters */}
      <div style={{
        background: 'linear-gradient(135deg, #f8f9ff 0%, #e0e7ff 100%)',
        padding: 25,
        borderRadius: 20,
        marginBottom: 30,
        border: '3px solid #667eea'
      }}>
        {/* Search Bar */}
        <div style={{ marginBottom: 20 }}>
          <label style={{
            display: 'flex',
            alignItems: 'center',
            gap: 8,
            marginBottom: 10,
            color: '#667eea',
            fontWeight: 'bold',
            fontSize: '1.1em'
          }}>
            <span>🔍</span> Search Lessons
          </label>
          <input
            type="text"
            placeholder="Search by title or content..."
            value={search}
            onChange={e => setSearch(e.target.value)}
            style={{
              width: '100%',
              padding: '15px',
              fontSize: '1.1em',
              borderRadius: 15
            }}
          />
        </div>

        {/* Filter Buttons */}
        <div style={{ display: 'flex', gap: 15, flexWrap: 'wrap', alignItems: 'center' }}>
          {/* Grade Filter */}
          <div>
            <label style={{ fontSize: '0.9em', color: '#667eea', fontWeight: 'bold', display: 'block', marginBottom: 8 }}>
              📊 Filter by Grade:
            </label>
            <div style={{ display: 'flex', gap: 8 }}>
              {['all', 1, 2, 3].map(g => (
                <button
                  key={g}
                  onClick={() => setGrade(g)}
                  className="btn"
                  style={{
                    background: grade === g ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' : '#f0f0f0',
                    color: grade === g ? 'white' : '#667eea',
                    padding: '10px 20px',
                    fontSize: '1em'
                  }}
                >
                  {g === 'all' ? '🌐 All' : `${getGradeEmoji(g)} Grade ${g}`}
                </button>
              ))}
            </div>
          </div>

          {/* Subject Filter */}
          <div>
            <label style={{ fontSize: '0.9em', color: '#667eea', fontWeight: 'bold', display: 'block', marginBottom: 8 }}>
              📚 Filter by Subject:
            </label>
            <div style={{ display: 'flex', gap: 8 }}>
              {['all', 'English', 'Math', 'Science'].map(s => (
                <button
                  key={s}
                  onClick={() => setSubject(s.toLowerCase())}
                  className="btn"
                  style={{
                    background: subject === s.toLowerCase() ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' : '#f0f0f0',
                    color: subject === s.toLowerCase() ? 'white' : '#667eea',
                    padding: '10px 20px',
                    fontSize: '1em'
                  }}
                >
                  {s === 'all' ? '🌐 All' : `${getSubjectEmoji(s)} ${s}`}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Results Counter */}
        <div style={{
          marginTop: 15,
          padding: 12,
          background: 'white',
          borderRadius: 10,
          textAlign: 'center',
          fontWeight: 'bold',
          color: '#667eea'
        }}>
          📊 Showing {lessons.length} of {allLessons.length} lessons
        </div>
      </div>

      {/* Recently Completed Section */}
      {user && recentCompletions.length > 0 && (
        <div style={{ marginBottom: 30 }}>
          <h4 style={{ color: '#667eea', fontSize: '1.8em', display: 'flex', alignItems: 'center', gap: 10 }}>
            <span>🏆</span> Recently Completed
          </h4>
          <div style={{ display: 'grid', gap: 12 }}>
            {recentCompletions.map(p => (
              <div
                key={p._id}
                style={{
                  background: 'linear-gradient(135deg, #d4edda 0%, #c3e6cb 100%)',
                  padding: 15,
                  borderRadius: 15,
                  border: '3px solid #28a745',
                  display: 'flex',
                  alignItems: 'center',
                  gap: 15
                }}
              >
                <span style={{ fontSize: '2em' }}>✅</span>
                <div style={{ flex: 1 }}>
                  <strong style={{ color: '#155724', fontSize: '1.2em' }}>{p.lesson?.title}</strong>
                  <div style={{ fontSize: '0.9em', color: '#155724', marginTop: 4 }}>
                    Grade {p.lesson?.grade} • Score: {p.score}/100
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Loading State */}
      {loading && (
        <div style={{ textAlign: 'center', padding: 60 }}>
          <div style={{ fontSize: '4em', animation: 'pulse 1.5s ease-in-out infinite' }}>📚</div>
          <p style={{ fontSize: '1.3em', color: '#667eea', marginTop: 15 }}>Loading lessons...</p>
        </div>
      )}

      {/* Lessons Grouped by Unit */}
      {!loading && lessons.length > 0 && (
        <div>
          {Object.keys(groupedLessons).sort((a, b) => a - b).map(unit => (
            <div key={unit} style={{ marginBottom: 30 }}>
              <div style={{
                background: getGradeColor(grade !== 'all' ? Number(grade) : 1),
                padding: 15,
                borderRadius: 15,
                marginBottom: 15,
                display: 'flex',
                alignItems: 'center',
                gap: 10
              }}>
                <span style={{ fontSize: '2em' }}>📁</span>
                <h4 style={{ color: 'white', margin: 0, fontSize: '1.5em' }}>
                  Unit {unit} ({groupedLessons[unit].length} lessons)
                </h4>
              </div>

              <div style={{ display: 'grid', gap: 15 }}>
                {groupedLessons[unit].map(lesson => {
                  const completed = isLessonCompleted(lesson._id);
                  return (
                    <div
                      key={lesson._id}
                      className="lesson"
                      style={{
                        padding: 20,
                        background: completed
                          ? 'linear-gradient(135deg, #d4edda 0%, #c3e6cb 100%)'
                          : 'white',
                        borderRadius: 20,
                        border: `3px solid ${completed ? '#28a745' : '#e0e7ff'}`,
                        position: 'relative',
                        transition: 'all 0.3s ease'
                      }}
                    >
                      {completed && (
                        <div style={{
                          position: 'absolute',
                          top: 15,
                          right: 15,
                          background: '#28a745',
                          color: 'white',
                          padding: '8px 15px',
                          borderRadius: 50,
                          fontSize: '0.9em',
                          fontWeight: 'bold',
                          display: 'flex',
                          alignItems: 'center',
                          gap: 5
                        }}>
                          ✓ Completed
                        </div>
                      )}

                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', gap: 15 }}>
                        <div style={{ flex: 1 }}>
                          <div style={{ display: 'flex', alignItems: 'center', gap: 10, marginBottom: 10 }}>
                            <span style={{ fontSize: '1.5em' }}>{getGradeEmoji(lesson.grade)}</span>
                            <strong style={{ fontSize: '1.4em', color: '#667eea' }}>
                              {lesson.title}
                            </strong>
                          </div>

                          <div style={{ display: 'flex', gap: 10, marginBottom: 12, flexWrap: 'wrap' }}>
                            <span style={{
                              background: getGradeColor(lesson.grade),
                              color: 'white',
                              padding: '6px 15px',
                              borderRadius: 50,
                              fontSize: '0.9em',
                              fontWeight: 'bold'
                            }}>
                              Grade {lesson.grade}
                            </span>

                            {lesson.subject && (
                              <span style={{
                                background: 'linear-gradient(135deg, #74b9ff 0%, #0984e3 100%)',
                                color: 'white',
                                padding: '6px 15px',
                                borderRadius: 50,
                                fontSize: '0.9em',
                                fontWeight: 'bold'
                              }}>
                                {getSubjectEmoji(lesson.subject)} {lesson.subject}
                              </span>
                            )}

                            {lesson.audioUrl && (
                              <span style={{
                                background: 'linear-gradient(135deg, #ffeaa7 0%, #fdcb6e 100%)',
                                color: '#d63031',
                                padding: '6px 15px',
                                borderRadius: 50,
                                fontSize: '0.9em',
                                fontWeight: 'bold',
                                animation: 'pulse 2s ease-in-out infinite'
                              }}>
                                🎵 Audio Available
                              </span>
                            )}
                          </div>

                          <p style={{
                            fontSize: '1.05em',
                            color: '#6b7280',
                            lineHeight: 1.7,
                            marginBottom: 0
                          }}>
                            {lesson.textContent.substring(0, 120)}...
                          </p>
                        </div>

                        <button
                          className="btn"
                          onClick={() => setSelected(lesson)}
                          style={{
                            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                            padding: '15px 30px',
                            fontSize: '1.1em',
                            whiteSpace: 'nowrap'
                          }}
                        >
                          📖 Open Lesson
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Empty State */}
      {!loading && lessons.length === 0 && (
        <div style={{
          textAlign: 'center',
          padding: 60,
          background: 'linear-gradient(135deg, #f8f9ff 0%, #e0e7ff 100%)',
          borderRadius: 20,
          border: '3px dashed #667eea'
        }}>
          <div style={{ fontSize: '5em', marginBottom: 20 }}>🔍</div>
          <h3 style={{ color: '#667eea', fontSize: '2em', marginBottom: 10 }}>
            No lessons found
          </h3>
          <p style={{ fontSize: '1.2em', color: '#6b7280' }}>
            Try adjusting your filters or search terms
          </p>
          <button
            className="btn"
            onClick={() => {
              setGrade('all');
              setSubject('all');
              setSearch('');
            }}
            style={{
              marginTop: 20,
              padding: '15px 35px',
              fontSize: '1.1em'
            }}
          >
            🔄 Reset Filters
          </button>
        </div>
      )}

      {/* Lesson Player Modal */}
      {selected && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.7)',
          zIndex: 1000,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          padding: 20,
          overflowY: 'auto'
        }}>
          <div style={{ maxWidth: 900, width: '100%' }}>
            <LessonPlayer
              lesson={selected}
              user={user}
              onClose={() => {
                setSelected(null);
                fetchProgress();
                fetchStats();
              }}
            />
          </div>
        </div>
      )}

      <style>{`
        @keyframes pulse {
          0%, 100% { opacity: 1; transform: scale(1); }
          50% { opacity: 0.8; transform: scale(1.05); }
        }
      `}</style>
    </div>
  );
}
