import React, { useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import RoleSelect from './components/RoleSelect';
import ChallengeSelect from './components/ChallengeSelect';
import DyslexiaHome from './components/DyslexiaHome';
import ParentDashboard from './components/ParentDashboard';
import Teacher from './components/teacher';
function App(){
  const [user,setUser] = useState(JSON.parse(localStorage.getItem('user')) || null);
  const navigate = useNavigate();
  const handleLogin = (u) => { setUser(u); localStorage.setItem('user', JSON.stringify(u)); navigate('/role'); }
  const handleLogout = ()=>{ setUser(null); localStorage.removeItem('user'); navigate('/login'); }
  return (
    <div className="container">
      <div className="card header">
        <div style={{flex:1}}>
          <h2 style={{color:'#0b63d6',margin:0}}>DyslexiaLearner</h2>
          <div className="small">Simple, dyslexia-friendly learning up to Grade 3</div>
        </div>
        <div>
          {user ? (
            <div style={{display:'flex',gap:8,alignItems:'center'}}>
              <div className="small">{user.name} ({user.role})</div>
              <button className="btn" onClick={handleLogout}>Logout</button>
            </div>
          ) : (
            <button className="btn" onClick={()=>navigate('/login')}>Login / Register</button>
          )}
        </div>
      </div>
      <Routes>
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/role" element={<RoleSelect user={user} />} />
        <Route path="/challenge" element={<ChallengeSelect />} />
        <Route path="/dyslexia" element={<DyslexiaHome user={user} />} />
        <Route path="/parent-dashboard" element={<ParentDashboard user={user} />} />
        <Route path="/teacher-dashboard" element={<Teacher user={user} />} />
        <Route path="/" element={<div style={{marginTop:20}} className="card"><h3>Welcome â€” start by logging in</h3></div>} />
      </Routes>
    </div>
  );
}
export default App;
