import React, { useEffect, useState } from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';

export default function ParentDashboard({ user }) {
  const [progress, setProgress] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const nav = useNavigate();

  useEffect(() => {
    if (user) {
      load();
    }
  }, [user]);

  async function load() {
    try {
      setLoading(true);
      const p = await API(`/api/progress/${user.id}`);
      setProgress(p);

      // Calculate stats
      const completed = p.filter(item => item.completed).length;
      const total = p.length;
      const percentage = total > 0 ? Math.round((completed / total) * 100) : 0;

      // Grade breakdown
      const grade1 = p.filter(item => item.lesson?.grade === 1 && item.completed).length;
      const grade2 = p.filter(item => item.lesson?.grade === 2 && item.completed).length;
      const grade3 = p.filter(item => item.lesson?.grade === 3 && item.completed).length;

      // Average score
      const completedLessons = p.filter(item => item.completed);
      const avgScore = completedLessons.length > 0
        ? Math.round(completedLessons.reduce((sum, item) => sum + item.score, 0) / completedLessons.length)
        : 0;

      setStats({
        completed,
        total,
        percentage,
        grade1,
        grade2,
        grade3,
        avgScore
      });
    } catch (err) {
      console.error(err);
      alert('Could not load progress data');
    } finally {
      setLoading(false);
    }
  }

  function getGradeColor(grade) {
    const colors = {
      1: 'linear-gradient(135deg, #ffeaa7 0%, #fdcb6e 100%)',
      2: 'linear-gradient(135deg, #74b9ff 0%, #0984e3 100%)',
      3: 'linear-gradient(135deg, #fab1a0 0%, #ff7675 100%)'
    };
    return colors[grade] || colors[1];
  }

  function getGradeEmoji(grade) {
    const emojis = { 1: '🌟', 2: '⭐', 3: '✨' };
    return emojis[grade] || '📚';
  }

  // Sort by most recent
  const sortedProgress = [...progress].sort(
    (a, b) => new Date(b.lastAccessed) - new Date(a.lastAccessed)
  );

  const recentCompletions = sortedProgress.filter(p => p.completed).slice(0, 5);
  const inProgress = sortedProgress.filter(p => !p.completed);

  return (
    <div className="card" style={{ marginTop: 20 }}>
      {/* Header */}
      <div
        style={{
          background: 'linear-gradient(135deg, #fab1a0 0%, #ff7675 100%)',
          padding: 35,
          borderRadius: 20,
          color: 'white',
          marginBottom: 30,
          position: 'relative',
          overflow: 'hidden'
        }}
      >
        <div
          style={{
            position: 'absolute',
            top: -80,
            right: -80,
            width: 250,
            height: 250,
            background: 'rgba(255,255,255,0.1)',
            borderRadius: '50%',
            filter: 'blur(50px)'
          }}
        ></div>

        <div style={{ position: 'relative', zIndex: 1 }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: 15, marginBottom: 15 }}>
            <div style={{ fontSize: '3.5em' }}>👨‍👩‍👧</div>
            <div>
              <h3 style={{ margin: 0, fontSize: '2.5em' }}>Parent Dashboard</h3>
              <p style={{ margin: 0, opacity: 0.95, fontSize: '1.2em' }}>
                Track your child's learning journey
              </p>
            </div>
          </div>

          {/* Quick Actions */}
          <div style={{ display: 'flex', gap: 10, marginTop: 20, flexWrap: 'wrap' }}>
            <button
              className="btn"
              onClick={() => nav('/dyslexia')}
              style={{
                background: 'rgba(255,255,255,0.95)',
                color: '#ff7675',
                padding: '12px 25px',
                border: 'none',
                borderRadius: 10,
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              📘 Go to Lessons
            </button>

            <button
              className="btn"
              onClick={() => load()}
              style={{
                background: 'rgba(255,255,255,0.95)',
                color: '#ff7675',
                padding: '12px 25px',
                border: 'none',
                borderRadius: 10,
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              🔄 Refresh Data
            </button>
          </div>
        </div>
      </div>

      {/* Stats Section */}
      {loading ? (
        <p>Loading...</p>
      ) : stats ? (
        <>
          <div
            style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
              gap: 20,
              marginBottom: 30
            }}
          >
            <div
              style={{
                background: '#ffeaa7',
                padding: 20,
                borderRadius: 15,
                textAlign: 'center'
              }}
            >
              <h4>Completed Lessons</h4>
              <p style={{ fontSize: '1.8em', margin: 0 }}>{stats.completed}/{stats.total}</p>
            </div>

            <div
              style={{
                background: '#fab1a0',
                padding: 20,
                borderRadius: 15,
                textAlign: 'center'
              }}
            >
              <h4>Completion Rate</h4>
              <p style={{ fontSize: '1.8em', margin: 0 }}>{stats.percentage}%</p>
            </div>

            <div
              style={{
                background: '#74b9ff',
                padding: 20,
                borderRadius: 15,
                textAlign: 'center'
              }}
            >
              <h4>Average Score</h4>
              <p style={{ fontSize: '1.8em', margin: 0 }}>{stats.avgScore}</p>
            </div>
          </div>

          {/* Grade Breakdown */}
          <div style={{ marginBottom: 30 }}>
            <h3>Grade-wise Progress</h3>
            <div style={{ display: 'flex', gap: 20, flexWrap: 'wrap' }}>
              {[1, 2, 3].map(grade => (
                <div
                  key={grade}
                  style={{
                    background: getGradeColor(grade),
                    padding: 20,
                    borderRadius: 15,
                    flex: '1 1 200px',
                    color: 'white'
                  }}
                >
                  <h4>{getGradeEmoji(grade)} Grade {grade}</h4>
                  <p style={{ fontSize: '1.5em', margin: 0 }}>
                    {stats[`grade${grade}`]} lessons completed
                  </p>
                </div>
              ))}
            </div>
          </div>

          {/* Recent Completions */}
          <div>
            <h3>Recent Lesson Activity</h3>
            {recentCompletions.length === 0 ? (
              <p>No recent completed lessons yet.</p>
            ) : (
              <ul>
                {recentCompletions.map((item, i) => (
                  <li key={i}>
                    ✅ {item.lesson?.title} — Score: {item.score}
                  </li>
                ))}
              </ul>
            )}
          </div>
        </>
      ) : (
        <p>No data available</p>
      )}
    </div>
  );
}
