import React, { useEffect, useState } from 'react';
import API from '../api';
import LessonPlayer from './LessonPlayer';
import { useNavigate } from 'react-router-dom';
export default function DyslexiaHome({ user }){
  const [lessons,setLessons] = useState([]);
  const [grade,setGrade] = useState(1);
  const [selected,setSelected] = useState(null);
  const nav = useNavigate();
  useEffect(()=>{ fetchLessons(grade); },[grade]);
  async function fetchLessons(g){
    try{ const data = await API(`/api/lessons?challenge=dyslexia&grade=${g}`); setLessons(data); }
    catch(err){ alert('Could not load lessons') }
  }
  return (
    <div className="card" style={{marginTop:20}}>
      <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
        <h3>Dyslexia â€” Grade {grade}</h3>
        <div>
          <label className="small">Grade</label>
          <select value={grade} onChange={e=>setGrade(Number(e.target.value))}>
            <option value={1}>Grade 1</option>
            <option value={2}>Grade 2</option>
            <option value={3}>Grade 3</option>
          </select>
          {user && user.role === 'parent' && <button style={{marginLeft:8}} className="btn" onClick={()=>nav('/parent-dashboard')}>Parent Dashboard</button>}
        </div>
      </div>
      <p className="small">Each lesson has dyslexia-friendly text and audio. Use the Read Aloud button for text-to-speech.</p>
      {lessons.map(l=> (
        <div className="lesson" key={l._id}>
          <div style={{display:'flex',justifyContent:'space-between'}}>
            <div>
              <strong>{l.title}</strong>
              <div className="small">Grade {l.grade}</div>
            </div>
            <div>
              <button className="btn" onClick={()=>setSelected(l)}>Open</button>
            </div>
          </div>
        </div>
      ))}
      {selected && <LessonPlayer lesson={selected} user={user} onClose={()=>setSelected(null)} />}
    </div>
  );
}
