import React, { useEffect, useState } from 'react';
import API from '../api';
export default function ParentDashboard({ user }){
  const [progress, setProgress] = useState([]);
  useEffect(()=>{ if(user){ load(); } },[user]);
  async function load(){
    try{
      const p = await API(`/api/progress/${user.id}`);
      setProgress(p);
    }catch(err){ console.error(err); }
  }
  return (
    <div className="card" style={{marginTop:20}}>
      <h3>Parent Dashboard</h3>
      <p className="small">This dashboard shows progress for linked students. To demo: create student accounts and add relationships in DB.</p>
      <div>
        {progress.length === 0 && <div className="small">No progress to show yet.</div>}
        {progress.map(pr => (
          <div key={pr._id} className="lesson">
            <div style={{display:'flex',justifyContent:'space-between'}}>
              <div>
                <strong>{pr.lesson?.title}</strong>
                <div className="small">Completed: {pr.completed ? 'Yes' : 'No'} â€” Score: {pr.score}</div>
              </div>
              <div className="small">Last: {new Date(pr.lastAccessed).toLocaleString()}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
