import React from 'react';

export default function LessonPlayer({ lesson = {}, onClose }) {
  const audioPath = `${process.env.PUBLIC_URL}/audio/grade${lesson.grade || 1}/${lesson.audioUrl}`;

  return (
    <div
      className="card"
      style={{
        marginTop: 20,
        background: 'linear-gradient(135deg, #fff 0%, #f8f9ff 100%)',
        border: '3px solid #667eea',
        boxShadow: '0 10px 40px rgba(102, 126, 234, 0.3)',
        padding: 20,
        borderRadius: 15,
      }}
    >
      <h2 style={{ color: '#2d3436' }}>{lesson.title || 'Untitled Lesson'}</h2>
      <h4 style={{ color: '#636e72' }}>Grade {lesson.grade || '1'}</h4>

      <div
        style={{
          background: '#fff5e1',
          padding: 20,
          borderRadius: 10,
          marginTop: 20,
          border: '2px solid #fdcb6e',
        }}
      >
        <p>{lesson.textContent || 'ðŸ“– Lesson content will appear here.'}</p>
      </div>

      {/* âœ… Audio Player */}
      {lesson.audioUrl ? (
        <div
          style={{
            marginTop: 20,
            background: '#d4edda',
            padding: 15,
            borderRadius: 10,
            border: '2px solid #28a745',
          }}
        >
          <strong>ðŸŽµ Listen to Audio:</strong>
          <audio
            controls
            src={audioPath}
            style={{ width: '100%', marginTop: 10 }}
            onError={(e) => console.error('Audio not found:', e)}
          />
        </div>
      ) : (
        <p style={{ color: '#666', marginTop: 20 }}>ðŸŽ§ No audio available for this lesson.</p>
      )}

      <button
        onClick={onClose}
        style={{
          marginTop: 20,
          padding: '10px 20px',
          background: '#ff7675',
          color: 'white',
          border: 'none',
          borderRadius: 8,
          cursor: 'pointer',
        }}
      >
        Close
      </button>
    </div>
  );
}
