import React, { useState, useEffect } from 'react';
import API from '../api';

export default function LessonPlayer({ lesson, user, onClose }) {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [rate, setRate] = useState(0.9);

  useEffect(() => {
    return () => stopSpeak();
  }, []);

  function speak(text) {
    if (!('speechSynthesis' in window)) return alert('üîá Text-to-Speech not supported in this browser');
    stopSpeak();
    const u = new SpeechSynthesisUtterance(text);
    u.lang = 'en-US';
    u.rate = rate;
    u.onend = () => setIsSpeaking(false);
    setIsSpeaking(true);
    window.speechSynthesis.speak(u);
  }

  function stopSpeak() {
    window.speechSynthesis.cancel();
    setIsSpeaking(false);
  }

  async function markComplete() {
    if (!user) return alert('üîí Please login to save progress');
    try {
      await API('/api/progress', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: user.id,
          userRole: user.role,
          lesson: lesson._id,
          completed: true,
          score: 100
        })
      });
      alert('üéâ Progress saved! Great job!');
    } catch (err) {
      alert('‚ùå Could not save progress');
    }
  }

  function getGradeColor(grade) {
    const colors = {
      1: 'linear-gradient(135deg, #ffeaa7 0%, #fdcb6e 100%)',
      2: 'linear-gradient(135deg, #74b9ff 0%, #0984e3 100%)',
      3: 'linear-gradient(135deg, #fab1a0 0%, #ff7675 100%)'
    };
    return colors[grade] || colors[1];
  }

  function getGradeEmoji(grade) {
    const emojis = { 1: 'üåü', 2: '‚≠ê', 3: '‚ú®' };
    return emojis[grade] || 'üìö';
  }

  return (
    <div className="card" style={{
      marginTop: 20,
      background: 'linear-gradient(135deg, #fff 0%, #f8f9ff 100%)',
      border: '3px solid #667eea',
      boxShadow: '0 10px 40px rgba(102, 126, 234, 0.3)'
    }}>
      {/* Header Section */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: 25,
        padding: 20,
        background: getGradeColor(lesson.grade),
        borderRadius: 15,
        boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
      }}>
        <div style={{ flex: 1 }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: 10, marginBottom: 8 }}>
            <span style={{ fontSize: '2em' }}>{getGradeEmoji(lesson.grade)}</span>
            <h4 style={{
              color: 'white',
              fontSize: '2em',
              margin: 0,
              textShadow: '2px 2px 4px rgba(0,0,0,0.2)'
            }}>
              {lesson.title}
            </h4>
          </div>
          <div style={{
            background: 'rgba(255,255,255,0.9)',
            color: '#667eea',
            padding: '6px 15px',
            borderRadius: 50,
            fontSize: '0.9em',
            fontWeight: 'bold',
            display: 'inline-block'
          }}>
            üìö Grade {lesson.grade}
          </div>
        </div>
        <button
          className="btn"
          onClick={onClose}
          style={{
            background: 'linear-gradient(135deg, #ff7675 0%, #d63031 100%)',
            padding: '12px 24px',
            fontSize: '1.1em'
          }}
        >
          ‚úï Close
        </button>
      </div>

      {/* Lesson Content */}
      <div style={{
        background: 'linear-gradient(135deg, #ffeaa7 0%, #fff5e1 100%)',
        padding: 30,
        borderRadius: 20,
        margin: '20px 0',
        border: '3px solid #fdcb6e',
        fontSize: '1.4em',
        lineHeight: 2.2,
        color: '#2d3436',
        boxShadow: '0 6px 20px rgba(253, 203, 110, 0.2)',
        letterSpacing: '0.5px'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 10, marginBottom: 15 }}>
          <span style={{ fontSize: '1.5em' }}>üìñ</span>
          <strong style={{ color: '#d63031', fontSize: '1em' }}>Lesson Content:</strong>
        </div>
        {lesson.textContent}
      </div>

      {/* Audio Player Section - FIXED */}
      {lesson.audioUrl && (
        <div style={{
          background: 'linear-gradient(135deg, #d4edda 0%, #c3e6cb 100%)',
          padding: 20,
          borderRadius: 15,
          margin: '20px 0',
          border: '3px solid #28a745',
          boxShadow: '0 6px 20px rgba(40, 167, 69, 0.2)'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: 10, marginBottom: 12 }}>
            <span style={{ fontSize: '1.8em' }}>üéµ</span>
            <strong style={{ color: '#155724', fontSize: '1.2em' }}>
              Listen to Audio Recording:
            </strong>
          </div>
          <audio
            controls
            src={lesson.audioUrl}
            style={{
              width: '100%',
              borderRadius: 50,
              outline: 'none'
            }}
            onError={(e) => {
              console.error('Audio error:', lesson.audioUrl);
              console.error('Error details:', e);
            }}
          />
          <div style={{ fontSize: '0.9em', color: '#155724', marginTop: 8 }}>
            Audio file: {lesson.audioUrl}
          </div>
        </div>
      )}

      {/* Control Panel */}
      <div style={{
        background: 'linear-gradient(135deg, #e0e7ff 0%, #f8f9ff 100%)',
        padding: 25,
        borderRadius: 15,
        border: '3px solid #667eea',
        marginTop: 20
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 10, marginBottom: 15 }}>
          <span style={{ fontSize: '1.5em' }}>üé§</span>
          <strong style={{ color: '#667eea', fontSize: '1.2em' }}>
            Text-to-Speech Controls:
          </strong>
        </div>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
          gap: 12,
          marginBottom: 20
        }}>
          <button
            className="btn"
            onClick={() => speak(lesson.textContent)}
            disabled={isSpeaking}
            style={{
              background: isSpeaking
                ? 'linear-gradient(135deg, #fdcb6e 0%, #ffeaa7 100%)'
                : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              padding: '15px 25px',
              fontSize: '1.1em'
            }}
          >
            {isSpeaking ? 'üîä Speaking...' : 'üîä Read Aloud'}
          </button>

          <button
            className="btn"
            onClick={stopSpeak}
            style={{
              background: 'linear-gradient(135deg, #ff7675 0%, #d63031 100%)',
              padding: '15px 25px',
              fontSize: '1.1em'
            }}
          >
            ‚èπÔ∏è Stop
          </button>
        </div>

        {/* Speed Control */}
        <div style={{
          background: 'white',
          padding: 20,
          borderRadius: 15,
          border: '2px solid #e0e7ff'
        }}>
          <label style={{
            fontWeight: 'bold',
            fontSize: '1.1em',
            color: '#667eea',
            display: 'block',
            marginBottom: 10
          }}>
            ‚ö° Reading Speed: {rate.toFixed(1)}x
          </label>
          <input
            type="range"
            min="0.6"
            max="1.4"
            step="0.1"
            value={rate}
            onChange={e => setRate(Number(e.target.value))}
            style={{
              width: '100%',
              height: 10,
              borderRadius: 50,
              cursor: 'pointer'
            }}
          />
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            marginTop: 8,
            fontSize: '0.85em',
            color: '#6b7280'
          }}>
            <span>üê¢ Slow (0.6x)</span>
            <span>Normal (1.0x)</span>
            <span>üêá Fast (1.4x)</span>
          </div>
        </div>
      </div>

      {/* Complete Button */}
      <div style={{
        marginTop: 25,
        textAlign: 'center'
      }}>
        <button
          className="btn"
          onClick={markComplete}
          style={{
            background: 'linear-gradient(135deg, #00b894 0%, #00cec9 100%)',
            padding: '20px 50px',
            fontSize: '1.3em',
            boxShadow: '0 8px 25px rgba(0, 184, 148, 0.4)'
          }}
        >
          ‚úì Mark as Complete
        </button>
      </div>
    </div>
  );
}
