import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function RoleSelect({ user }) {
  const nav = useNavigate();

  if (!user) return <div className="card" style={{marginTop:20}}>Please login first.</div>

  return (
    <div className="card" style={{marginTop:20}}>
      <h3>Welcome, {user.name}!</h3>
      <p className="small">You are logged in as: <strong>{user.role}</strong></p>

      <div style={{display:'flex', flexDirection:'column', gap:12, marginTop:20}}>
        {/* Student Options */}
        {user.role === 'student' && (
          <>
            <button className="btn" onClick={() => nav('/challenge')}>
              Go to Challenges
            </button>
            <button className="btn" onClick={() => nav('/dyslexia')}>
              Go to Lessons
            </button>
          </>
        )}

        {/* Parent Options */}
        {user.role === 'parent' && (
          <button className="btn" onClick={() => nav('/parent-dashboard')}>
            Parent Dashboard
          </button>
        )}

        {/* Teacher Options */}
        {user.role === 'teacher' && (
          <button className="btn" onClick={() => nav('/teacher-dashboard')}>
            Teacher Dashboard
          </button>
        )}
      </div>

      <p className="small" style={{marginTop:20}}>
        Choose an option above to continue.
      </p>
    </div>
  );
}
