import React, { useState } from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';
export default function Login({ onLogin }){
  const [mode,setMode] = useState('login');
  const [form,setForm] = useState({ name:'', email:'', password:'', role:'student' });
  const nav = useNavigate();
  async function submit(e){
    e.preventDefault();
    try{
      const url = mode === 'login' ? '/api/auth/login' : '/api/auth/register';
      const res = await API(url, { method:'POST', headers:{'Content-Type':'application/json'}, body: JSON.stringify(form) });
      onLogin(res.user);
      nav('/role');
    }catch(err){ alert(err.message || 'Error'); }
  }
  return (
    <div className="card" style={{marginTop:20}}>
      <h3>{mode==='login' ? 'Login' : 'Register'}</h3>
      <form onSubmit={submit} style={{display:'grid',gap:10}}>
        {mode!=='login' && (
          <input placeholder="Full name" value={form.name} onChange={e=>setForm({...form,name:e.target.value})} required />
        )}
        <input placeholder="Email" value={form.email} onChange={e=>setForm({...form,email:e.target.value})} required />
        <input placeholder="Password" type="password" value={form.password} onChange={e=>setForm({...form,password:e.target.value})} required />
        <label className="small">Role</label>
        <select value={form.role} onChange={e=>setForm({...form,role:e.target.value})}>
          <option value="student">Student</option>
          <option value="parent">Parent</option>
          <option value="teacher">Teacher</option>
        </select>
        <div style={{display:'flex',gap:8}}>
          <button className="btn" type="submit">{mode==='login' ? 'Login' : 'Register'}</button>
          <button type="button" className="btn" style={{background:'#6b7bff'}} onClick={()=>setMode(mode==='login' ? 'register' : 'login')}>{mode==='login' ? 'Switch to Register' : 'Switch to Login'}</button>
          <button type="button" onClick={()=>nav('/')} style={{marginLeft:'auto'}}>Cancel</button>
        </div>
      </form>
    </div>
  );
}
