import React, { useState, useEffect } from 'react';
import API from '../api';

export default function LessonPlayer({ lesson = {}, user, onClose }) {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [rate, setRate] = useState(0.9);

  useEffect(() => {
    return () => stopSpeak();
  }, []);

  function speak(text) {
    if (!('speechSynthesis' in window))
      return alert('üîá Text-to-Speech not supported in this browser');
    stopSpeak();
    const u = new SpeechSynthesisUtterance(text);
    u.lang = 'en-US';
    u.rate = rate;
    u.onend = () => setIsSpeaking(false);
    setIsSpeaking(true);
    window.speechSynthesis.speak(u);
  }

  function stopSpeak() {
    window.speechSynthesis.cancel();
    setIsSpeaking(false);
  }

  async function markComplete() {
    if (!user) return alert('üîí Please login to save progress');
    try {
      await API('/api/progress', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: user.id,
          userRole: user.role,
          lesson: lesson._id,
          completed: true,
          score: 100,
        }),
      });
      alert('üéâ Progress saved! Great job!');
    } catch (err) {
      alert('‚ùå Could not save progress');
    }
  }

  function getGradeColor(grade) {
    const colors = {
      1: 'linear-gradient(135deg, #ffeaa7 0%, #fdcb6e 100%)',
      2: 'linear-gradient(135deg, #74b9ff 0%, #0984e3 100%)',
      3: 'linear-gradient(135deg, #fab1a0 0%, #ff7675 100%)',
    };
    return colors[grade] || colors[1];
  }

  function getGradeEmoji(grade) {
    const emojis = { 1: 'üåü', 2: '‚≠ê', 3: '‚ú®' };
    return emojis[grade] || 'üìö';
  }

  // ‚úÖ Handle audio safely
  const audioPath = lesson.audioUrl
    ? lesson.audioUrl.startsWith('/audio/')
      ? `${process.env.PUBLIC_URL}${lesson.audioUrl}`
      : lesson.audioUrl.startsWith('http')
      ? lesson.audioUrl
      : `${process.env.PUBLIC_URL}/audio/grade${lesson.grade || 1}/${lesson.audioUrl}`
    : null;

  return (
    <div
      className="card"
      style={{
        marginTop: 20,
        background: 'linear-gradient(135deg, #fff 0%, #f8f9ff 100%)',
        border: '3px solid #667eea',
        boxShadow: '0 10px 40px rgba(102, 126, 234, 0.3)',
      }}
    >
      {/* Header Section */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: 25,
          padding: 20,
          background: getGradeColor(lesson.grade),
          borderRadius: 15,
          boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
        }}
      >
        <div style={{ flex: 1 }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: 10, marginBottom: 8 }}>
            <span style={{ fontSize: '2em' }}>{getGradeEmoji(lesson.grade)}</span>
            <h4
              style={{
                color: 'white',
                fontSize: '2em',
                margin: 0,
                textShadow: '2px 2px 4px rgba(0,0,0,0.2)',
              }}
            >
              {lesson.title || 'Untitled Lesson'}
            </h4>
          </div>
          <span
            style={{
              background: 'rgba(255,255,255,0.9)',
              color: '#667eea',
              padding: '6px 15px',
              borderRadius: 50,
              fontSize: '0.9em',
              fontWeight: 'bold',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
            }}
          >
            üìö Grade {lesson.grade || '1'}
          </span>
        </div>
        <button
          className="btn"
          onClick={onClose}
          style={{
            background: 'linear-gradient(135deg, #ff7675 0%, #d63031 100%)',
            padding: '12px 24px',
            fontSize: '1.1em',
          }}
        >
          ‚úï Close
        </button>
      </div>

      {/* Lesson Content */}
      <div
        style={{
          background: 'linear-gradient(135deg, #ffeaa7 0%, #fff5e1 100%)',
          padding: 30,
          borderRadius: 20,
          margin: '20px 0',
          border: '3px solid #fdcb6e',
          fontSize: '1.4em',
          lineHeight: 2.2,
          color: '#2d3436',
          boxShadow: '0 6px 20px rgba(253, 203, 110, 0.2)',
          letterSpacing: '0.5px',
        }}
      >
        {lesson.textContent || 'üìñ Lesson content will appear here.'}
      </div>

      {/* ‚úÖ Audio Player */}
      {audioPath ? (
        <div
          style={{
            background: 'linear-gradient(135deg, #d4edda 0%, #c3e6cb 100%)',
            padding: 20,
            borderRadius: 15,
            margin: '20px 0',
            border: '3px solid #28a745',
          }}
        >
          <strong style={{ color: '#155724', fontSize: '1.2em' }}>
            üéµ Listen to Audio:
          </strong>
          <audio
            controls
            src={audioPath}
            style={{ width: '100%', borderRadius: 50, marginTop: 10 }}
            onError={(e) => console.error('Audio error:', e)}
          />
        </div>
      ) : (
        <p style={{ color: '#666', fontStyle: 'italic', textAlign: 'center' }}>
          üéß No audio available for this lesson.
        </p>
      )}

      {/* Text-to-speech controls */}
      <div
        style={{
          background: 'linear-gradient(135deg, #e0e7ff 0%, #f8f9ff 100%)',
          padding: 25,
          borderRadius: 15,
          border: '3px solid #667eea',
          marginTop: 20,
        }}
      >
        <button
          className="btn"
          onClick={() => speak(lesson.textContent)}
          disabled={isSpeaking}
          style={{
            background: isSpeaking
              ? 'linear-gradient(135deg, #fdcb6e 0%, #ffeaa7 100%)'
              : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            padding: '15px 25px',
            fontSize: '1.1em',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: 8,
            width: '100%',
          }}
        >
          {isSpeaking ? 'üîä Speaking...' : 'üîä Read Aloud'}
        </button>

        <button
          className="btn"
          onClick={stopSpeak}
          style={{
            background: 'linear-gradient(135deg, #ff7675 0%, #d63031 100%)',
            padding: '15px 25px',
            fontSize: '1.1em',
            display: 'block',
            margin: '15px auto 0',
          }}
        >
          ‚èπÔ∏è Stop
        </button>
      </div>

      {/* Complete Button */}
      <div style={{ marginTop: 25, textAlign: 'center' }}>
        <button
          className="btn"
          onClick={markComplete}
          style={{
            background: 'linear-gradient(135deg, #00b894 0%, #00cec9 100%)',
            padding: '20px 50px',
            fontSize: '1.3em',
          }}
        >
          ‚úì Mark as Complete
        </button>
      </div>
    </div>
  );
}
